   492	        // Smooth scrolling for navigation links
   493	        document.querySelectorAll('a[href^="#"]').forEach(anchor => {
   494	            anchor.addEventListener('click', function (e) {
   495	                e.preventDefault();
   496	                const target = document.querySelector(this.getAttribute('href'));
   497	                if (target) {
   498	                    target.scrollIntoView({
   499	                        behavior: 'smooth',
