   506	        // Add scroll effect to navigation
   507	        window.addEventListener('scroll', () => {
   508	            const nav = document.querySelector('nav');
   509	            if (window.scrollY > 100) {
   510	                nav.style.background = 'rgba(255, 255, 255, 0.98)';
   511	                nav.style.boxShadow = '0 2px 20px rgba(0, 0, 0, 0.1)';
   512	            } else {
   513	                nav.style.background = 'rgba(255, 255, 255, 0.95)';
   514	                nav.style.boxShadow = 'none';
   515	            }
   516	        });
