   518	        // Intersection Observer for fade-in animations
   519	        const observerOptions = {
   520	            threshold: 0.1,
   521	            rootMargin: '0px 0px -50px 0px'
   522	        };
   523	
   524	        const observer = new IntersectionObserver((entries) => {
   525	            entries.forEach(entry => {
   526	                if (entry.isIntersecting) {
   527	                    entry.target.style.opacity = '1';
   528	                    entry.target.style.transform = 'translateY(0)';
   529	                }
   530	            });
   531	        }, observerOptions);
